import React, { useState, useEffect } from "react";

function AccountsPage(props) {

    const [accType, setAccType] = useState('bank');
    const [accName, setAccName] = useState();
    const [accCode, setAccCode] = useState();
    const [accCurrency, setAccCurency] = useState();
    const [accNum, setAccNum] = useState();
    const [bankName, setbankName] = useState();
    const [IFSC, setIFSC] = useState();
    const [description, setDescription] = useState();
    const [accountsList, setAccountsList] = useState([]);

    useEffect(() => {
        const getDataFromBank = async () => {
            try {
                const response = await fetch('http://localhost:8000/setbankaccount/');
                if (!response.ok) {
                    throw new Error('Fetch Problem')
                }
                const data = await response.json();
                setAccountsList(() => data);
                console.log(data);
            } catch (error) {
                console.error(error);
            }
        }
        getDataFromBank();
    }, []);

    const handleSubmitBankingDet = (e) => {
        e.preventDefault();
        setAccCurency(() => document.getElementById('currencyChosen').value);
        const postDataAccDet = async () => {
            try {
                if (accType == 'bank') {
                    const response = await fetch('http://127.0.0.1:8000/setbankaccount/', {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({ 'username': props.username, 'acc_name': accName, 'acc_code': accCode, 'currency': accCurrency, 'acc_num': accNum, 'bank_name': bankName, 'ifsc': IFSC, 'description': description }),
                    });
                    if (!response.ok) {
                        throw new Error('Response Error');
                    }
                } else {
                    const response = await fetch('http://localhost:8000/setcardaccount/', {
                        method: 'POST',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({ 'username': props.username, 'acc_name': accName, 'acc_code': accCode, 'currency': accCurrency, 'bank_name': bankName, 'description': description }),
                    });
                    if (!response.ok) {
                        throw new Error('Response Error');
                    }
                }
            } catch (error) {
                console.error(error);
            }
        }
        console.log('username', props.username, 'acc_name', accName, 'acc_code', accCode, 'currency', accCurrency, 'bank_name', bankName, 'description', description);
        postDataAccDet();
    }

    return (<>
        <div className="container-fluid p-0">
            <nav className="navbar bg-body-tertiary border-bottom">
                <div className="container-fluid">
                    <label className="nabar-brand ms-2 fs-1" style={{ fontWeight: "400" }}>
                        Banking&nbsp;Overview
                    </label>
                    <ul className="navbar-nav flex-row ms-auto">
                        <li className="nav-item m-1">
                            <button className="nav-link btn btn-secondary px-4 py-2 text-dark" data-bs-toggle="modal" data-bs-target="#ImportStatments">
                                Import Statments
                            </button>
                        </li>
                        <li className="nav-item m-1">
                            <button className="nav-link btn btn-secondary px-4 py-2 text-dark" data-bs-toggle="modal" data-bs-target="#AddBankCredit">
                                Add Bank or Credit Card
                            </button>
                        </li>
                    </ul>
                </div>
            </nav>

            <div className="modal fade" id="ImportStatments" tabindex="-1" aria-labelledby="ImportStatmentsLabel" aria-hidden="true">
                <div className="modal-dialog modal-xl modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title ms-2 fs-2" id="ImportStatmentsLabel">Import Statments</h1>
                            <button type="button" className="btn-close me-2" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>

                        <div className="modal-body fs-4">
                            Sorry for Inconvinence. This Function is currently not available.
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary px-5 py-2 fs-5" data-bs-dismiss="modal">Close</button>
                        </div>
                    </div>
                </div>
            </div>

            <div className="modal fade" id="AddBankCredit" tabindex="-1" aria-labelledby="AddBankCreditLabel" aria-hidden="true">
                <div className="modal-dialog modal-xl modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h1 className="modal-title ms-4 fs-2" id="ImportStatmentsLabel">Add Bank or Credit Card</h1>
                            <button type="button" className="btn-close me-2" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body fs-5">
                            <form action="" method="post" onSubmit={handleSubmitBankingDet}>
                                <div className="row mt-4">
                                    <div className="col-3 offset-md-1">
                                        <label htmlFor="accountTypeBankingPage">
                                            Select Account Type
                                        </label>
                                    </div>
                                    <div className="col-3 form-check text-start ms-2 offset-md-0 offset-3">
                                        <input type="radio" className="form-check-input" onClick={() => (accType == 'cCard' && setAccType(() => 'bank'))} checked={accType == 'bank'} name="selectAccountTypeBankingPageRadioBtn" id="selectAccountTypeBankingPageRadioBtnBank" />
                                        <label className="form-check-label" onClick={() => (accType == 'cCard' && setAccType('bank'))} htmlFor="selectAccountTypeBankingPageRadioBtnBank">&nbsp;Bank</label>
                                    </div>
                                    <div className="col-3 form-check text-start">
                                        <input type="radio" className="form-check-input" onClick={() => (accType == 'bank' && setAccType(() => 'cCard'))} name="selectAccountTypeBankingPageRadioBtn" id="selectAccountTypeBankingPageRadioBtnCard" />
                                        <label className="form-check-label" onClick={() => (accType == 'bank' && setAccType(() => 'cCard'))} htmlFor="selectAccountTypeBankingPageRadioBtnCard">&nbsp;Credit&nbsp;Card</label>
                                    </div>
                                </div>
                                <div className="row mt-4">
                                    <div className="col-3 fs-5 offset-md-1">
                                        Account&nbsp;Name
                                    </div>
                                    <div className="col-6  offset-md-0 offset-2">
                                        <input type="text" className="form-control" value={accName} onChange={(e) => setAccName(() => e.target.value)} name="" id="" />
                                    </div>
                                </div>
                                <div className="row mt-4">
                                    <div className="col-3 fs-5 offset-md-1">
                                        Account&nbsp;Code
                                    </div>
                                    <div className="col-6  offset-md-0 offset-2">
                                        <input type="text" className="form-control" value={accCode} onChange={(e) => setAccCode(() => e.target.value)} name="" id="" />
                                    </div>
                                </div>
                                <div className="row mt-4">
                                    <div className="col-3 fs-5 offset-md-1">
                                        Currency
                                    </div>
                                    <div className="col-6 offset-md-0 offset-2">
                                        <select name="" className="form-select text-center" value={'INR'} id="currencyChosen">
                                            <option value="INR">INR</option>
                                            <option value="USD">USD</option>
                                            <option value="CAD">CAD</option>
                                            <option value="EUR">EUR</option>
                                        </select>
                                    </div>
                                </div>
                                {accType == 'bank' &&
                                    <div className="row mt-4">
                                        <div className="col-3 fs-5 offset-md-1">
                                            Account&nbsp;Number
                                        </div>
                                        <div className="col-6 offset-md-0 offset-2">
                                            <input type="text" className="form-control" value={accNum} onChange={(e) => setAccNum(() => e.target.value)} name="" id="" />
                                        </div>
                                    </div>
                                }
                                <div className="row mt-4">
                                    <div className="col-3 fs-5 offset-md-1">
                                        Bank&nbsp;Name
                                    </div>
                                    <div className="col-6 offset-md-0 offset-2">
                                        <input type="text" className="form-control" value={bankName} onChange={(e) => setbankName(() => e.target.value)} name="" id="" />
                                    </div>
                                </div>
                                {accType == 'bank' &&
                                    <div className="row mt-4">
                                        <div className="col-3 fs-5 offset-md-1">
                                            IFSC
                                        </div>
                                        <div className="col-6 offset-md-0 offset-2">
                                            <input type="text" className="form-control" value={IFSC} onChange={(e) => setIFSC(() => e.target.value)} name="" id="" />
                                        </div>
                                    </div>
                                }
                                <div className="row mt-4">
                                    <div className="col-3 fs-5 offset-md-1">
                                        Description
                                    </div>
                                    <div className="col-6 offset-md-0 offset-2">
                                        <textarea name="" className="form-control" placeholder="Max. 500 characters" value={description} onChange={(e) => setDescription(() => e.target.value)} id="" cols="10" rows="3"></textarea>
                                    </div>
                                </div>
                                <div className="row mt-3">
                                    <div className="col-6 text-end">
                                        <button className="btn btn-success px-4" type="submit" data-bs-dismiss="modal">
                                            Save
                                        </button>
                                    </div>
                                    <div className="col-6 text-start">
                                        <button className="btn btn-secondary px-4" type="button" data-bs-dismiss="modal">
                                            Cancel
                                        </button>
                                    </div>
                                </div>

                            </form>
                        </div>
                    </div>
                </div>
            </div>
            <table className="table">
                <th>
                    <td>Account Type</td>
                    <td>Account Name</td>
                </th>
                {accountsList.map(element => {
                    return (
                        <tr>
                            <td>{element.username}</td>
                            <td>{element.acc_name}</td>
                        </tr>
                    );
                })}
            </table>
        </div >
    </>);
}

export default AccountsPage;
