import Chart from 'chart.js/auto';
import { Bar, Doughnut, Line } from "react-chartjs-2";

import React, { useState, useEffect } from "react";

function RevenuePage(props) {

    const [purchaseList, setPurchaseList] = useState([]);
    const [salesList, setSalesList] = useState([]);

    useEffect(() => {
        const getDataForPurchaseList = async () => {
            try {
                const response = await fetch('http://localhost:8000/getpurchaseorderlist/', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ "username": props.username }),
                });
                if (!response.ok) {
                    throw new Error('Fetching Error')
                }
                const data = await response.json();

                setPurchaseList(() => data);
            } catch (error) {
                console.error(error);
            }
        };
        getDataForPurchaseList();

        const getDataForSalesList = async () => {
            try {
                const response = await fetch('http://localhost:8000/getslaesorderlist/', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ "username": props.username }),
                });
                if (!response.ok) {
                    throw new Error('Fetching Error')
                }
                const data = await response.json();

                setSalesList(() => data);
            } catch (error) {
                console.error(error);
            }
        };
        getDataForSalesList();
    }, []);


    const handleNewPurchaseSubmit = (e) => {
        e.preventDefault();
        const getDataForPurchaseList = async () => {
            try {
                const response = await fetch('http://localhost:8000/getpurchaseorderlist/', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ "username": props.username }),
                });
                if (!response.ok) {
                    throw new Error('Fetching Error')
                }
                const data = await response.json();
                setPurchaseList(() => data);
                console.log(purchaseList);
            } catch (error) {
                console.error(error);
            }
        };
        getDataForPurchaseList();

        const getDataForSalesList = async () => {
            try {
                const response = await fetch('http://localhost:8000/getslaesorderlist/', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ "username": props.username }),
                });
                if (!response.ok) {
                    throw new Error('Fetching Error')
                }
                const data = await response.json();

                setSalesList(() => data);
            } catch (error) {
                console.error(error);
            }
        };
        getDataForSalesList();
    }

