import './App.css';
import { useState, useEffect } from 'react';
import UserEntry from './UserEntry';
import HomePage from './HomePage';

function App() {
  const [logedIn, setLogedIn] = useState(null); // Initial state to indicate loading
  const [uname, setUname] = useState('');
  const [isLoading, setIsLoading] = useState(true); // Track data fetching status

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/userstatus/');
        if (!response.ok) {
          throw new Error('Http Response Error');
        }
        const data = await response.json();
        setLogedIn(data.user_status);
        setUname(data.username);
      } catch (error) {
        console.error(error);
      } finally {
        setIsLoading(false); // Ensure loading state updates even on errors
      }
    };

    fetchData();
  }, []);

  const changeLoginState = (val) => {
    setLogedIn(val);
  };

  const setUsername = (val) => {
    setUname(val);
  };

  return (
    <div className="App">
      {isLoading ? (
        <p>Loading user data...</p>
      ) : (
        logedIn ? (
          <HomePage changeLoginState={changeLoginState} />
        ) : (
          <UserEntry changeLoginState={changeLoginState} setUsername={setUsername} />
        )
      )}
    </div>
  );
}

export default App;
