import './App.css';
import { useState } from 'react'; // Removed unnecessary import of useEffect
import { Redirect } from 'react-router-dom';
import UserEntry from './UserEntry';
import HomePage from './HomePage';

function App() {
  const [loggedIn, setLoggedIn] = useState(false); // Initialize loggedIn state to false
  const [uname, setUname] = useState('');

  const changeLoginState = (val) => {
    setLoggedIn(val); // Set loggedIn state based on the received value
  };

  const setUsername = (val) => {
    setUname(val);
  };

  // Redirect to "/home" if loggedIn state is true
  if (loggedIn) {
    return <Redirect to="/home" />;
  }

  return (
    <div className="App">
      {/* Render UserEntry or HomePage based on loggedIn state */}
      {loggedIn ? <HomePage changeLoginState={changeLoginState} /> : <UserEntry changeLoginState={changeLoginState} setUsername={setUsername} />}
    </div>
  );
}

export default App;
