          throw new Error('HTTP Response Error');
        }
        const data = await response.json();
        changeLoginState(data.data_present);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }

  const changeLoginState = (val) => {
    setLogedIn(() => val)
  };

  return (
    <div className="App">
      {logedIn ? <HomePage checkForUserDet={checkForUserDet} /> : <UserEntry checkForUserDet={checkForUserDet} />}
    </div>
  );
}

export default App;
