import './App.css';
import { useState, useEffect } from 'react';
import UserEntry from './UserEntry';
import HomePage from './HomePage'

function App() {
  const [logedIn, setLogedIn] = useState();
  const [uname, setUname] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/checkuser/');
        if (!response.ok) {
          throw new Error('HTTP Response Error');
        }
        const data = await response.json();
        changeLoginState(data.data_present);
        console.log(data);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }, [logedIn]);

  const changeLoginState = (val) => {
    setLogedIn(() => val)
  };

  const setUsername = (val) => {
    setUname(() => val);
  };

  return (
    <div className="App">
      {logedIn ? <HomePage changeLoginState={changeLoginState} /> : <UserEntry changeLoginState={changeLoginState} setUsername={setUsername} />}
    </div>
  );
}

export default App;
