import { useState, useEffect } from 'react';
import UserEntry from './UserEntry';
import HomePage from './HomePage'

function App() {
  const [logedIn, setLogedIn] = useState();
  const [uname, setUname] = useState('');

  function checkForUserDet() {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/checkuser/', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ 'username': uname, 'password': password }),
        });
        if (!response.ok) {
          throw new Error('HTTP Response Error');
        }
        const data = await response.json();
        setUserAccount({ 'Username': uname });
        props.changeLoginState(data.data_present);
      } catch (error) {
        console.error(error);
      }
    }
    fetchData();
  }

  const changeLoginState = (val) => {
    setLogedIn(() => val)
  };

  return (
    <div className="App">
      {logedIn ? <HomePage changeLoginState={changeLoginState} /> : <UserEntry changeLoginState={changeLoginState} />}
    </div>
  );
}

export default App;
